import React, { useEffect, useState } from "react";
import { Container, Row, Col, Card } from "react-bootstrap";
import ArticleCard from "../components/ArticleCard";
import axios from "axios";

const Home = () => {
  const [articles, setArticles] = useState([]);

  useEffect(() => {
    axios.get(`${process.env.REACT_APP_API_URL}/articles`)
      .then(response => setArticles(response.data))
      .catch(error => console.error(error));
  }, []);

  return (
    <Container>
      {/* Banner inicial continua inalterado */}
      <div id="newsCarousel" className="carousel slide my-4" data-bs-ride="carousel">
        <div className="carousel-inner">
          <div className="carousel-item active text-center p-5 bg-dark text-light">
            <h1>Bem-vindo ao TechNews!</h1>
            <p>Explore as √∫ltimas not√≠cias e tend√™ncias em tecnologia.</p>
          </div>
          <div className="carousel-item text-center p-5 bg-secondary text-light">
            <h3>Intelig√™ncia Artificial</h3>
            <p>Descubra como a IA est√° transformando o mundo.</p>
          </div>
          <div className="carousel-item text-center p-5 bg-dark text-light">
            <h3>Inova√ß√µes em Gadgets</h3>
            <p>Conhe√ßa os gadgets mais recentes do mercado.</p>
          </div>
        </div>
        <button className="carousel-control-prev" type="button" data-bs-target="#newsCarousel" data-bs-slide="prev">
          <span className="carousel-control-prev-icon" aria-hidden="true"></span>
          <span className="visually-hidden">Anterior</span>
        </button>
        <button className="carousel-control-next" type="button" data-bs-target="#newsCarousel" data-bs-slide="next">
          <span className="carousel-control-next-icon" aria-hidden="true"></span>
          <span className="visually-hidden">Pr√≥ximo</span>
        </button>
      </div>

      {/* Se√ß√£o de Destaques */}
      {articles.length > 0 && (
        <>
          <h2 className="my-4 text-primary">üîπ Destaques</h2>
          <Row>
            {articles.slice(0, 3).map(article => ( // Pegamos as 3 mais novas
              <Col key={article._id} md={4} className="mb-4">
                <Card className="shadow-lg border-0" style={{ backgroundColor: "#f8f9fa" }}>
                  <Card.Body>
                    <h4 className="fw-bold">{article.title}</h4>
                    <p className="text-muted">
                      Publicado em: {new Date(article.createdAt).toLocaleDateString("pt-BR", {
                        day: "2-digit", month: "long", year: "numeric"
                      })}
                    </p>
                    <p>{article.content.substring(0, 100)}...</p>
                    <a href={`/article/${article._id}`} className="btn btn-outline-primary">Leia mais</a>
                  </Card.Body>
                </Card>
              </Col>
            ))}
          </Row>
        </>
      )}

      {/* Se√ß√£o de √öltimas Not√≠cias */}
      <h1 className="my-4">√öltimas Not√≠cias</h1>
      <Row>
        {articles.length > 3 ? (
          articles.slice(3).map(article => ( // Mostramos o restante das not√≠cias
            <Col key={article._id} md={4} className="mb-4">
              <ArticleCard article={article} />
            </Col>
          ))
        ) : (
          <p className="text-center">Nenhuma outra not√≠cia encontrada.</p>
        )}
      </Row>
    </Container>
  );
};

export default Home;
