import React, { useEffect, useState } from "react";
import { Container, Row, Col, Card } from "react-bootstrap";
import ArticleCard from "../components/ArticleCard";
import axios from "axios";

const Home = () => {
  const [articles, setArticles] = useState([]);

  useEffect(() => {
    axios.get(`${process.env.REACT_APP_API_URL}/articles`)
      .then(response => setArticles(response.data))
      .catch(error => console.error(error));
  }, []);

  return (
    <Container>
      {/* Destaque para a notícia mais recente */}
      {articles.length > 0 && (
        <Card className="mb-4 p-3 shadow-sm" style={{ backgroundColor: "#f8f9fa" }}>
          <Card.Body>
            <h2 className="fw-bold">{articles[0].title}</h2>
            <p className="text-muted">
              Publicado em: {new Date(articles[0].createdAt).toLocaleDateString("pt-BR", {
                day: "2-digit", month: "long", year: "numeric"
              })}
            </p>
            <p>{articles[0].content.substring(0, 250)}...</p>
            <a href={`/article/${articles[0]._id}`} className="btn btn-primary">Leia mais</a>
          </Card.Body>
        </Card>
      )}

      <h1 className="my-4">Últimas Notícias</h1>
      <Row>
        {articles.length > 1 ? (
          articles.slice(1).map(article => ( // ✅ Agora excluímos a primeira notícia (destaque)
            <Col key={article._id} md={4} className="mb-4">
              <ArticleCard article={article} />
            </Col>
          ))
        ) : (
          <p className="text-center">Nenhuma outra notícia encontrada.</p>
        )}
      </Row>
    </Container>
  );
};

export default Home;
